INSERT INTO `` (`id`,`name`,`pe`,`power`,`duration`,`location`,`description`,`img`,`available`,`idCategory`) VALUES (130,'Topacio','0','0','0','Minerales, Petrarok luminoso, enemigos plateados.','Material que proviene de un octorok. Es demasiado ácido para usarlo con fines culinarios, pero resulta muy útil para preparar elixires.','https://vignette.wikia.nocookie.net/zelda/images/c/ca/Tent%C3%A1culo_de_octorok_BotW.png/revision/latest?cb=20170701225522&path-prefix=es',1,NULL);
