INSERT INTO `` (`id`,`name`,`pe`,`power`,`duration`,`location`,`description`,`img`,`available`,`idCategory`) VALUES (137,'Trufa vivaz','8','★','0','Cordillera de Hebra, Gran bosque de Hyrule.','La sustancia bioluminiscente que con la que brilla en la oscuridad sirve a su vez para aumentar la concentración. Al cocinarla, permite moverse sin generar ningún sonido.','https://vignette.wikia.nocookie.net/zelda/images/5/5f/Trucha_sigilosa_BotW.png/revision/latest?cb=20170321215113&path-prefix=es',1,NULL);
